import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'Hello! How can I help you today?' },
  ]);
  const [input, setInput] = useState('');

  const sendMessage = () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { from: 'user', text: input }];
    setMessages(newMessages);
    setInput('');

    // Simulate bot response
    setTimeout(() => {
      setMessages((prev) => [...prev, { from: 'bot', text: 'This is a response from AI.' }]);
    }, 1000);
  };

  return (
    <div className="container py-4" style={{ maxWidth: '720px' }}>
      <div className="bg-light rounded p-4 shadow-sm" style={{ height: '80vh', display: 'flex', flexDirection: 'column' }}>
        <h3 className="text-center mb-3">AI Chat Assistant</h3>

        <div className="flex-grow-1 overflow-auto mb-3" style={{ border: '1px solid #dee2e6', borderRadius: '8px', padding: '10px', background: '#f8f9fa' }}>
          {messages.map((msg, index) => (
            <div key={index} className={`text-${msg.from === 'bot' ? 'start' : 'end'} my-2`}>
              <span className={`badge bg-${msg.from === 'bot' ? 'secondary' : 'primary'}`}>{msg.text}</span>
            </div>
          ))}
        </div>

        <div className="d-flex gap-2">
          <input
            type="text"
            className="form-control"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          />
          <button className="btn btn-primary" onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
