// src/App.js
import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { type: "user", text: input }];
    setMessages(newMessages);
    setInput("");

    try {
      const res = await axios.post("http://127.0.0.1:5000/", {
        code: input,
        language: "Python"
      });

      const botMessage = res.data.result || `❌ ${res.data.error}`;
      setMessages([...newMessages, { type: "bot", text: botMessage }]);
    } catch (error) {
      setMessages([
        ...newMessages,
        { type: "bot", text: "❌ Could not connect to server." }
      ]);
    }
  };

  return (
    <div className="container">
      <div className="header">Chatbot</div>
      <div className="chat-area">
        <div className="left-box">
          {messages.map((msg, i) => (
            <div
              key={i}
              className={`chat-message ${msg.type === "user" ? "user-message" : "ai-message"}`}
              dangerouslySetInnerHTML={{ __html: msg.text.replace(/\n/g, "<br>") }}
            />
          ))}
        </div>
        <div className="right-box">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your code..."
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
